INSERT INTO `sby_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `icon`) VALUES
(1, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connettiti a WooCommerce.com', 'Connettiti per ottenere notifiche e aggiornamenti sui prodotti importanti.', '{}', 'unactioned', 'woocommerce-admin', '2020-11-23 18:51:09', NULL, 0, 'plain', '', 0, 'info'),
(2, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Benvenuto in WooCommerce! Configura il tuo negozio e inizia a vendere', 'Siamo qui per aiutarti a portare a termine i passaggi pi importanti per rendere operativo il tuo negozio.', '{}', 'actioned', 'woocommerce-admin', '2020-11-23 16:51:09', NULL, 0, 'plain', '', 0, 'info'),
(3, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Suggerimenti, aggiornamenti sui prodotti e ispirazione', 'Siamo qui per te. Ricevi suggerimenti, aggiornamenti di prodotto e ispirazione direttamente nella casella di posta elettronica', '{}', 'unactioned', 'woocommerce-admin', '2020-11-23 18:51:09', NULL, 0, 'plain', '', 0, 'info'),
(4, 'wc-admin-marketing-intro', 'info', 'en_US', 'Collegati al tuo pubblico', 'Fai crescere la base di clienti e aumenta le vendite con gli strumenti di marketing integrati per WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2020-11-23 18:51:09', NULL, 0, 'plain', '', 0, 'info'),
(5, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. Were happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-11-20 00:51:09', NULL, 0, 'plain', '', 0, 'info'),
(6, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store ready to sell as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2020-11-20 00:51:09', NULL, 0, 'plain', '', 0, 'info'),
(7, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while theyre shopping on your online store. No more follow-up email requestscustomers get what they want, before theyre done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-11-20 00:51:09', NULL, 0, 'plain', '', 0, 'info'),
(8, 'wcpay-promo-2020-11', 'marketing', 'en_US', 'Manage subscriber payments from your store\'s dashboard', 'Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>   now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>', '{}', 'pending', 'woocommerce.com', '2020-11-20 00:51:09', NULL, 0, 'plain', '', 0, 'info'),
(9, 'wcpay-subscriptions-2020-11', 'marketing', 'en_US', 'Manage subscriber payments from your store\'s dashboard', 'Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>   now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>', '{}', 'pending', 'woocommerce.com', '2020-11-20 00:51:09', NULL, 0, 'plain', '', 0, 'info'),
(10, 'mailchimp-for-woocommerce-incomplete-install', 'warning', 'en_US', 'Mailchimp For WooCommerce', 'Plugin is not yet connected to a Mailchimp account. To complete the connection, open the settings page.', '{\"getting_started\":true,\"activated\":1606161228,\"activated_formatted\":\"November 23rd\"}', 'unactioned', 'mailchimp-for-woocommerce', '2020-11-23 18:53:48', NULL, 0, 'plain', '', 0, 'info'),
(12, 'wc-admin-mobile-app', 'info', 'en_US', 'Installa l\'app Woo', 'Installa l\'app per dispositivi mobili WooCommerce per gestire gli ordini, riceve le notifiche delle vendite e visualizzare le metriche chiave, ovunque tu sia.', '{}', 'unactioned', 'woocommerce-admin', '2020-11-26 11:10:22', NULL, 0, 'plain', '', 0, 'info'),
(13, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Aiuta a migliorare WooCommerce con il tracciamento dell\'uso', 'Raccogliere i dati di utilizzo ci consente di migliorare WooCommerce. Il tuo negozio verr tenuto in considerazione nel momento in cui valuteremo nuove funzioni, giudicheremo la qualit di un aggiornamento o determineremo se un miglioramento ha senso. Puoi sempre visitare le <a href=\"https://localhost/shibayu//wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Impostazioni</a> e scegliere di interrompere la condivisione dei dati. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Scopri di pi</a> su quali dati raccogliamo.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-01 12:05:30', NULL, 0, 'plain', '', 0, 'info'),
(14, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Lasciaci un feedback', 'Ora che ci hai scelto come partner, il nostro obiettivo  quello di avere la certezza di fornirti i giusti strumenti per soddisfare i tuoi bisogni. Non vediamo l\'ora di ricevere il tuo feedback sull\'esperienza di configurazione del negozio in modo da poterla migliorare in futuro.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-01 18:51:31', NULL, 0, 'plain', '', 0, 'info'),
(15, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Lo sapevi?', 'Un negozio promosso da WooCommerce ha bisogno di una media di 31 giorni per ottenere la prima vendita. Stai andando nella giusta direzione! Hai trovato utile questo tipo di panoramica?', '{}', 'unactioned', 'woocommerce-admin', '2020-12-01 18:51:31', NULL, 0, 'plain', '', 0, 'info'),
(16, 'wc-admin-orders-milestone', 'info', 'en_US', 'Congratulazioni per l\'elaborazione dei 10 ordini!', 'Hai raggiunto il traguardo dei 10 ordini! Guarda come procedi. Dai un\'occhiata alle storie di successo di WooCommerce per trarre ispirazione.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-02 11:09:15', NULL, 0, 'plain', '', 0, 'info'),
(18, 'wc-admin-home-screen-feedback', 'info', 'en_US', 'Aiutaci a migliorare la schermata principale di WooCommerce', 'Apprezzeremmo molto il tuo contributo per creare insieme il futuro della schermata principale di WooCommerce. Non esitare a condividere qualsiasi feedback, idea o suggerimento che hai.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-06 07:05:54', NULL, 0, 'plain', '', 0, 'info'),
(20, 'wcpay-promo-2020-12', 'marketing', 'en_US', 'Get 50% off transaction fees with WooCommerce Payments', 'Keep more of your hard-earned cash by adding <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp20\" target=\"_blank\">WooCommerce Payments</a> to your store. Lock in a discounted rate of 1.5% + $0.15 for $25,000 of payments (or three months, whichever comes first). Limited time offer  dont miss out! <br /><br /><em>By clicking \"Install now,\" you agree to our promotional <a href=\"https://woocommerce.com/terms-conditions/woocommerce-payments-promotion/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp20\" target=\"_blank\">Terms of Service</a>.</em>', '{}', 'pending', 'woocommerce.com', '2020-12-09 12:28:27', NULL, 0, 'plain', '', 0, 'info'),
(21, 'wcpay-subscriptions-2020-12', 'marketing', 'en_US', 'Manage payments from your store\'s dashboard', 'Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>! Zero setup fees or monthly fees. Just pay-as-you-go, starting at just 2.9% + $0.30 per transaction for U.S.-issued cards. <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>', '{}', 'pending', 'woocommerce.com', '2020-12-09 12:28:27', NULL, 0, 'plain', '', 0, 'info'),
(22, 'wc-admin-new-sales-record', 'info', 'en_US', 'Nuovo record di vendite!', 'Evviva, December 16th  stato il giorno il cui hai raggiunto il record delle vendite! Le vendite nette sono state di 453,60 battendo il tuo record precedente di 115,00 raggiunto il giorno December 7th.', '{\"old_record_date\":\"2020-12-07\",\"old_record_amt\":115,\"new_record_date\":\"2020-12-16\",\"new_record_amt\":453.59999999999997}', 'unactioned', 'woocommerce-admin', '2020-12-17 18:51:55', NULL, 0, 'plain', '', 0, 'info');
